import React, { useState, useEffect, useCallback } from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';

// Using original component names
import MetricsSummary from './MetricsSummary';
import RealTimeChart from './RealTimeChart_1';
import TransactionHistory from './TransactionHistory';
import FraudAlerts from './FraudAlerts';
import TransactionDetails from './TransactionDetails';
import { Transaction, Prediction, Metrics } from '../types/types';

const FraudMonitor = () => {
  // State management
  const [transactionData, setTransactionData] = useState([]);
  const [predictionResults, setPredictionResults] = useState([]);
  const [activeTransaction, setActiveTransaction] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [serverStatus, setServerStatus] = useState('initializing');
  const [stats, setStats] = useState({
    total: 0,
    flagged: 0,
    recentVolume: 0
  });

  // Connection management with retry logic
  useEffect(() => {
    let wsConnection = null;
    let reconnectAttempts = 0;
    const maxReconnectAttempts = 5;

    const connectToServer = () => {
      setServerStatus('initializing');
      wsConnection = new WebSocket("ws://localhost:8000/ws/fraud_detection/");

      wsConnection.onopen = () => {
        console.log("Monitoring connection established");
        setServerStatus('online');
        reconnectAttempts = 0;
      };

      wsConnection.onmessage = (event) => {
        const payload = JSON.parse(event.data);

        // Handle system messages
        if (payload.message) {
          console.log(`System notification: ${payload.message}`);
          return;
        }

        // Process transaction data
        if (payload.transaction) {
          handleNewTransaction(payload);
        }
      };

      wsConnection.onerror = (error) => {
        console.error("Connection error:", error);
        setServerStatus('error');
      };

      wsConnection.onclose = (event) => {
        setServerStatus('offline');

        // Implement exponential backoff for reconnection
        if (reconnectAttempts < maxReconnectAttempts) {
          const timeout = Math.pow(2, reconnectAttempts) * 1000;
          console.log(`Attempting reconnection in ${timeout/1000} seconds...`);

          setTimeout(() => {
            reconnectAttempts++;
            connectToServer();
          }, timeout);
        } else {
          console.error("Maximum reconnection attempts reached");
        }
      };
    };

    connectToServer();

    return () => {
      if (wsConnection) {
        wsConnection.close();
      }
    };
  }, []);

  // Process incoming transaction data
  const handleNewTransaction = useCallback((payload) => {
    const { transaction, predictions, prediction } = payload;

    // Normalize data structure
    const normalizedPrediction = predictions || prediction || null;

    if (transaction && normalizedPrediction) {
      // Add timestamp for visualization
      const enrichedTransaction = {
        ...transaction,
        receivedAt: new Date().toISOString()
      };

      // Update state with new data
      setTransactionData(prev => [...prev, enrichedTransaction]);
      setPredictionResults(prev => [...prev, normalizedPrediction]);

      // Update statistics
      setStats(prevStats => {
        const isFraudulent = isFlaggedAsFraud(normalizedPrediction);
        return {
          total: prevStats.total + 1,
          flagged: isFraudulent ? prevStats.flagged + 1 : prevStats.flagged,
          recentVolume: prevStats.recentVolume + (transaction.amount || 0)
        };
      });
    }
  }, []);

  // Helper function to determine if a transaction is flagged
  const isFlaggedAsFraud = (prediction) => {
    if (!prediction) return false;
    return prediction.xgboost === 1 || 
           prediction.random_forest === 1 || 
           prediction.logistic === 1;
  };

  // Event handlers
  const handleTransactionSelect = (index) => {
    setActiveTransaction({
      details: transactionData[index],
      prediction: predictionResults[index]
    });
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setActiveTransaction(null);
  };

  // Prepare data for model comparison chart
  const prepareModelComparisonData = () => {
    const modelData = [
      { name: 'Logistic Reg.', accuracy: 0.85, color: '#8884d8' },
      { name: 'Random Forest', accuracy: 0.92, color: '#82ca9d' },
      { name: 'XGBoost', accuracy: 0.95, color: '#ffc658' }
    ];
    return modelData;
  };

  const modelData = prepareModelComparisonData();

  // Get high risk transactions
  const highRiskTransactions = transactionData
    .map((transaction, index) => ({
      transaction,
      prediction: predictionResults[index]
    }))
    .filter(item => isFlaggedAsFraud(item.prediction))
    .slice(0, 5);

  return (
    <div className="bg-slate-50 min-h-screen">
      <header className="bg-gradient-to-r from-indigo-700 to-purple-800 text-white p-4 shadow-lg">
        <div className="container mx-auto flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold">Fraud Shield Monitor</h1>
            <p className="text-sm font-light">Real-time transaction monitoring system</p>
          </div>
          <StatusIndicator status={serverStatus} />
        </div>
      </header>

      <main className="container mx-auto p-4">
        {/* Dashboard Stats Row */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="bg-white rounded-lg shadow p-4">
            <h3 className="text-gray-500 text-sm font-medium mb-2">Total Transactions</h3>
            <p className="text-3xl font-bold">{stats.total}</p>
          </div>

          <div className="bg-white rounded-lg shadow p-4">
            <h3 className="text-gray-500 text-sm font-medium mb-2">Flagged Transactions</h3>
            <p className="text-3xl font-bold text-red-600">{stats.flagged}</p>
            <p className="text-sm text-gray-500">
              {stats.total > 0 ? `${((stats.flagged / stats.total) * 100).toFixed(1)}%` : '0%'} Fraud Rate
            </p>
          </div>

          <div className="bg-white rounded-lg shadow p-4">
            <h3 className="text-gray-500 text-sm font-medium mb-2">Transaction Volume</h3>
            <p className="text-3xl font-bold">
              ${stats.recentVolume.toLocaleString(undefined, { minimumFractionDigits: 2 })}
            </p>
          </div>
        </div>

        {/* Main Dashboard Content */}
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Left Column - Tables & Charts */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-lg shadow mb-6">
              <div className="border-b p-4">
                <h2 className="font-semibold text-lg">Model Performance Comparison</h2>
              </div>
              <div className="p-4 h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart
                    data={modelData}
                    margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                  >
                    <XAxis dataKey="name" />
                    <YAxis domain={[0, 1]} tickFormatter={(value) => `${value * 100}%`} />
                    <Tooltip formatter={(value) => `${(value * 100).toFixed(1)}%`} />
                    <Bar dataKey="accuracy" name="Accuracy Score" fill="#8884d8" />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow">
              <div className="border-b p-4 flex justify-between items-center">
                <h2 className="font-semibold text-lg">Transaction History</h2>
                <span className="text-sm text-gray-500">
                  {transactionData.length} transactions recorded
                </span>
              </div>
              <TransactionTable
                transactions={transactionData}
                predictions={predictionResults}
                onSelectTransaction={handleTransactionSelect}
              />
            </div>
          </div>

          {/* Right Column - Alerts & Risk Factors */}
          <div className="lg:col-span-1">
            <AlertPanel
              highRiskTransactions={highRiskTransactions}
              onViewDetails={handleTransactionSelect}
            />

            <div className="bg-white rounded-lg shadow mt-6">
              <div className="border-b p-4">
                <h2 className="font-semibold text-lg">Detection Model Overview</h2>
              </div>
              <div className="p-4">
                <ModelAccuracyWidget models={{
                  logistic: 0.85,
                  random_forest: 0.92,
                  xgboost: 0.95
                }} />
              </div>
            </div>
          </div>
        </div>
      </main>

      {/* Transaction Details Modal */}
      {isModalOpen && activeTransaction && (
        <TransactionModal
          transaction={activeTransaction.details}
          prediction={activeTransaction.prediction}
          onClose={closeModal}
        />
      )}
    </div>
  );
};

export default FraudMonitor;
