import React, { useState, useMemo } from 'react';
import { Transaction, Prediction } from '../types/types';

interface TransactionHistoryProps {
  transactions: Transaction[];
  predictions: Prediction[];
  onSelectTransaction: (transaction: Transaction) => void;
}

const TransactionHistory: React.FC<TransactionHistoryProps> = ({ 
  transactions, 
  predictions, 
  onSelectTransaction 
}) => {
  // State management
  const [sortConfig, setSortConfig] = useState<{
    key: keyof Transaction | 'Risk',
    direction: 'asc' | 'desc'
  }>({ key: 'Time', direction: 'desc' });

  const [currentPage, setCurrentPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState<'all' | 'fraud' | 'legitimate'>('all');

  const ITEMS_PER_PAGE = 6;

  // Determine transaction risk level
  const getRiskLevel = (transaction: Transaction, index: number): 'high' | 'medium' | 'low' => {
    // Check explicit Class if available
    if (transaction.Class === 1) {
      return 'high';
    }

    // Check prediction models
    if (index < predictions.length) {
      const prediction = predictions[index];
      // Count how many models flagged as fraudulent
      const fraudCount = [
        prediction.logistic, 
        prediction.random_forest, 
        prediction.xgboost
      ].filter(val => val === 1).length;

      if (fraudCount >= 2) return 'high';
      if (fraudCount === 1) return 'medium';
    }

    // Check transaction features
    if ((transaction.V1 !== undefined && transaction.V1 < -3) || 
        (transaction.Amount !== undefined && transaction.Amount > 1000)) {
      return 'medium';
    }

    return 'low';
  };

  // Format transaction time as a readable date in 2025
  const formatTransactionDate = (timeValue?: number): string => {
    if (timeValue === undefined) return 'Unknown';

    const baseDate = new Date('2025-01-01T00:00:00');
    const secondsInYear = 365 * 24 * 60 * 60;
    const resultDate = new Date(baseDate.getTime() + ((timeValue % secondsInYear) * 1000));

    // Format with more readable style
    return new Intl.DateTimeFormat('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    }).format(resultDate);
  };

  // Sort, filter, and search transactions
  const processedTransactions = useMemo(() => {
    // Start with a copy of the transactions array
    let result = [...transactions];

    // Apply search filter if query exists
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      result = result.filter(transaction => {
        const amount = transaction.Amount?.toString().toLowerCase() || '';
        const id = (transaction.transaction_id || '').toString().toLowerCase();
        const date = formatTransactionDate(transaction.Time).toLowerCase();

        return amount.includes(query) || id.includes(query) || date.includes(query);
      });
    }

    // Apply fraud/legitimate filter
    if (filterStatus !== 'all') {
      result = result.filter((transaction, index) => {
        const riskLevel = getRiskLevel(transaction, index);
        return (filterStatus === 'fraud' && riskLevel === 'high') || 
               (filterStatus === 'legitimate' && riskLevel !== 'high');
      });
    }

    // Apply sorting
    result.sort((a, b) => {
      // Special case for Risk sorting which isn't a direct property
      if (sortConfig.key === 'Risk') {
        const indexA = transactions.indexOf(a);
        const indexB = transactions.indexOf(b);
        const riskA = getRiskLevel(a, indexA);
        const riskB = getRiskLevel(b, indexB);

        // Convert risk levels to numeric values for sorting
        const riskValues = { high: 3, medium: 2, low: 1 };
        const comparison = riskValues[riskA] - riskValues[riskB];

        return sortConfig.direction === 'asc' ? comparison : -comparison;
      }

      // Regular property sorting
      const aValue = a[sortConfig.key as keyof Transaction];
      const bValue = b[sortConfig.key as keyof Transaction];

      // Handle undefined values
      if (aValue === undefined && bValue === undefined) return 0;
      if (aValue === undefined) return sortConfig.direction === 'asc' ? -1 : 1;
      if (bValue === undefined) return sortConfig.direction === 'asc' ? 1 : -1;

      // Regular comparison
      if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });

    return result;
  }, [transactions, predictions, sortConfig, searchQuery, filterStatus]);

  // Handle pagination
  const totalPages = Math.max(1, Math.ceil(processedTransactions.length / ITEMS_PER_PAGE));
  const paginatedTransactions = processedTransactions.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  // Handle sorting
  const requestSort = (key: keyof Transaction | 'Risk') => {
    setSortConfig(prevConfig => ({
      key,
      direction: prevConfig.key === key && prevConfig.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  // Render sort indicator
  const renderSortIndicator = (key: keyof Transaction | 'Risk') => {
    if (sortConfig.key !== key) return null;
    return sortConfig.direction === 'asc' ? (
      <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 15l7-7 7 7"></path>
      </svg>
    ) : (
      <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
      </svg>
    );
  };

  // Status badge styling based on risk level
  const getStatusBadge = (riskLevel: 'high' | 'medium' | 'low') => {
    switch (riskLevel) {
      case 'high':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
            <span className="w-2 h-2 mr-1 bg-red-500 rounded-full"></span>
            High Risk
          </span>
        );
      case 'medium':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
            <span className="w-2 h-2 mr-1 bg-yellow-500 rounded-full"></span>
            Medium Risk
          </span>
        );
      case 'low':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
            <span className="w-2 h-2 mr-1 bg-green-500 rounded-full"></span>
            Legitimate
          </span>
        );
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg border border-gray-200 overflow-hidden">
      {/* Header and Controls */}
      <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-4 border-b border-gray-200">
        <div className="flex flex-col md:flex-row md:justify-between md:items-center gap-4">
          <h2 className="text-lg font-semibold text-gray-800">
            Transaction History
            <span className="ml-2 text-sm font-normal text-gray-500">
              {processedTransactions.length} records
            </span>
          </h2>

          <div className="flex flex-col sm:flex-row gap-3">
            {/* Search box */}
            <div className="relative">
              <input
                type="text"
                className="w-full sm:w-64 pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 text-sm"
                placeholder="Search transactions..."
                value={searchQuery}
                onChange={(e) => {
                  setSearchQuery(e.target.value);
                  setCurrentPage(1); // Reset to first page on search
                }}
              />
              <div className="absolute left-3 top-2.5 text-gray-400">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path>
                </svg>
              </div>
            </div>

            {/* Transaction type filter */}
            <select
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 text-sm bg-white"
              value={filterStatus}
              onChange={(e) => {
                setFilterStatus(e.target.value as 'all' | 'fraud' | 'legitimate');
                setCurrentPage(1); // Reset to first page on filter change
              }}
            >
              <option value="all">All Transactions</option>
              <option value="fraud">High Risk Only</option>
              <option value="legitimate">Legitimate Only</option>
            </select>
          </div>
        </div>
      </div>

      {/* Transaction Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer select-none"
                onClick={() => requestSort('Time')}
              >
                <div className="flex items-center">
                  Date & Time
                  {renderSortIndicator('Time')}
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer select-none"
                onClick={() => requestSort('Amount')}
              >
                <div className="flex items-center">
                  Amount
                  {renderSortIndicator('Amount')}
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer select-none"
                onClick={() => requestSort('Risk')}
              >
                <div className="flex items-center">
                  Status
                  {renderSortIndicator('Risk')}
                </div>
              </th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                Actions
              </th>
            </tr>
          </thead>

          <tbody className="bg-white divide-y divide-gray-200">
            {paginatedTransactions.length > 0 ? (
              paginatedTransactions.map((transaction, index) => {
                const originalIndex = transactions.indexOf(transaction);
                const riskLevel = getRiskLevel(transaction, originalIndex);

                return (
                  <tr 
                    key={transaction.transaction_id || index}
                    className={`
                      ${riskLevel === 'high' ? 'bg-red-50' : riskLevel === 'medium' ? 'bg-yellow-50' : ''}
                      hover:bg-gray-50 transition-colors duration-150
                    `}
                  >
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900 font-medium">
                        {formatTransactionDate(transaction.Time)}
                      </div>
                      <div className="text-xs text-gray-500">
                        ID: {transaction.transaction_id || `TRANS-${originalIndex}`}
                      </div>
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm font-medium text-gray-900">
                        ${transaction.Amount !== undefined ? transaction.Amount.toFixed(2) : '0.00'}
                      </div>
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap">
                      {getStatusBadge(riskLevel)}
                    </td>

                    <td className="px-6 py-4 whitespace-nowrap text-right">
                      <button
                        onClick={() => onSelectTransaction(transaction)}
                        className="inline-flex items-center px-3 py-1.5 border border-transparent text-xs font-medium rounded-md text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                      >
                        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"></path>
                        </svg>
                        Details
                      </button>
                    </td>
                  </tr>
                );
              })
            ) : (
              <tr>
                <td colSpan={4} className="px-6 py-10 text-center text-gray-500">
                  <div className="flex flex-col items-center justify-center">
                    <svg className="w-12 h-12 text-gray-300 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                    </svg>
                    <span className="text-lg font-medium">No transactions found</span>
                    <p className="text-sm mt-1">Try adjusting your search or filter criteria</p>
                  </div>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination Controls */}
      <div className="bg-gray-50 px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
        <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
          <div>
            <p className="text-sm text-gray-700">
              Showing <span className="font-medium">{Math.min(processedTransactions.length, 1 + (currentPage - 1) * ITEMS_PER_PAGE)}</span> to{' '}
              <span className="font-medium">{Math.min(processedTransactions.length, currentPage * ITEMS_PER_PAGE)}</span> of{' '}
              <span className="font-medium">{processedTransactions.length}</span> results
            </p>
          </div>

          <div>
            <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
              <button
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className={`relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium ${
                  currentPage === 1 
                    ? 'text-gray-300 cursor-not-allowed' 
                    : 'text-gray-500 hover:bg-gray-50'
                }`}
              >
                <span className="sr-only">Previous</span>
                <svg className="h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                  <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
                </svg>
              </button>

              {/* Page number buttons - can be expanded to show more pages */}
              {Array.from({ length: Math.min(totalPages, 5) }, (_, i) => {
                // Calculate which page numbers to show based on current page
                let pageNum;
                if (totalPages <= 5) {
                  pageNum = i + 1;
                } else if (currentPage <= 3) {
                  pageNum = i + 1;
                } else if (currentPage >= totalPages - 2) {
                  pageNum = totalPages - 4 + i;
                } else {
                  pageNum = currentPage - 2 + i;
                }

                return (
                  <button
                    key={pageNum}
                    onClick={() => setCurrentPage(pageNum)}
                    className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                      currentPage === pageNum
                        ? 'z-10 bg-indigo-50 border-indigo-500 text-indigo-600'
                        : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                    }`}
                  >
                    {pageNum}
                  </button>
                );
              })}

              <button
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
                className={`relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium ${
                  currentPage === totalPages 
                    ? 'text-gray-300 cursor-not-allowed' 
                    : 'text-gray-500 hover:bg-gray-50'
                }`}
              >
                <span className="sr-only">Next</span>
                <svg className="h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                  <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
                </svg>
              </button>
            </nav>
          </div>
        </div>

        {/* Mobile pagination */}
        <div className="flex justify-between w-full sm:hidden">
          <button
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
            className={`relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md ${
              currentPage === 1 
                ? 'text-gray-300 bg-gray-50 cursor-not-allowed' 
                : 'text-gray-700 bg-white hover:bg-gray-50'
            }`}
          >
            Previous
          </button>
          <span className="text-sm text-gray-700">
            Page {currentPage} of {totalPages}
          </span>
          <button
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
            className={`relative inline-flex items-center px-4 py-2 ml-3 border border-gray-300 text-sm font-medium rounded-md ${
              currentPage === totalPages 
                ? 'text-gray-300 bg-gray-50 cursor-not-allowed' 
                : 'text-gray-700 bg-white hover:bg-gray-50'
            }`}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default TransactionHistory;
